CompatibleCalibrationSelection.SQL:
SELECT DISTINCTROW (ec.ECMCodeBase + '.' + ec.ECMCodeRev) AS ECMCodeExpr,   pjfr.FROption AS FROption,  ec.PJOption AS PJOption,  pjo.Environment AS Environment,  pjo.EngineModel AS EngineModel,  pjo.EmissionLevel AS EmissionLevel,  pjo.NumberOfCylinders AS NumberOfCylinders,  pjo.ModuleLocation AS ModuleLocation,  pjo.CalRestr AS CalibrationRestriction,  pjo.SoftwarePhase AS SoftwarePhase,  pjo.Comment AS Comment
 FROM ECMCODE AS ec, PJOPTION AS pjo, PJFR AS pjfr
 WHERE ec.ECMPartNumber = [ECMPartNumberValue] AND INSTR([CompatibleFROptionListValue], pjfr.FROption) > 0 AND ec.PJOption = pjfr.PJOption AND ec.PJOption = pjo.PJOption ORDER BY ec.ECMCodeBase, ec.ECMCodeRev;

CompatibleFROption.SQL:
SELECT pj.FROption AS FROption
 FROM PJFR AS pj, ECMCODE AS ec WHERE pj.PJOption = ec.PJOption AND ec.ECMCodeBase = [ECMCodeBaseValue] AND ec.ECMCodeRev = [ECMCodeRevValue] AND ec.ECMPartNumber = [ECMPartNumberValue]
 ORDER BY pj.FROption;

SupersessionTrail.SQL:
SELECT DISTINCTROW (ec1.ECMCodeBase + '.' + ec1.ECMCodeRev) AS OldECMCode, (ec2.ECMCodeBase + '.' + ec2.ECMCodeRev) AS NewECMCode FROM ECMCODE AS ec1, ECMCODE AS ec2, STATICCALREDUCTION AS cr WHERE  ec1.ECMPartNumber = [ECMPartNumberValue] AND ec1.SupersessionFlag LIKE 'E' AND cr.OldECMCodeBase = ec1.ECMCodeBase AND ec2.ECMCodeBase = cr.NewECMCodeBase AND ec2.ECMPartNumber = ec1.ECMPartNumber 
UNION SELECT (ec3.ECMCodeBase + '.' + ec3.ECMCodeRev) AS OldECMCode, (ec4.ECMCodeBase + '.' + ec4.ECMCodeRev) AS NewECMCode FROM ECMCODE AS ec3, SSTRAIL AS ss, ECMCODE AS ec4 WHERE    ec3.ECMPartNumber = [ECMPartNumberValue] AND ec3.SupersessionFlag LIKE 'Y' AND ss.OldOption=ec3.SCOption AND 
ec4.SCOption=ss.NewOption AND ec4.ECMPartNumber=ec3.ECMPartNumber;

CalibrationSelection.SQL:
SELECT DISTINCTROW (ec.ECMCodeBase + '.' + ec.ECMCodeRev) AS ECMCodeExpr, ppo.EngModel AS EngineModel, sco.PowerRating AS Market, ec.CPL AS CPL, (sco.AdvPower + ' hp @ ' + sco.AdvPowerRPM + ' rpm') AS AdvPowerExpr, (sco.GovernedPower + ' hp @ ' + sco.GovernedPowerRPM + ' rpm') AS GovernedPower, (sco.TorquePeak + ' lb-ft @ ' + sco.TorquePeakRPM + ' rpm') AS TorquePeakExpr, doo.Application AS ApplicationComments, doo.Transmission AS TransmissionType, sco.Calrestr AS PerformanceOptions,  'N/A' AS CPLComments, sco.SCComments AS SCComments, doo.DOComment AS DOComments, ec.Froption AS FROption, ec.SCOption AS SCOption, ec.DOOption AS DOOption,  ccr.result AS CertificationCodes FROM ECMCODE AS ec, DOOPTION AS doo, PPOPTION AS ppo, CERTCODE AS cc, APOPTION AS apo, SCOPTION AS sco, ECMAP AS eap, CERTCODERESULT AS ccr
 WHERE ec.ECMPartNumber = [ECMPartNumberValue] AND ((ec.ECMCodeBase)=[eap].[ECMCodeBase]) AND ((ec.ECMCodeRev)=[eap].[ECMCodeRev]) AND sco.SCOption = ec.SCOption AND cc.APOption=eap.APOption AND INSTR([SelectedCertCodesValue], cc.CertCode) > 0 AND ec.DOOption=doo.DOOption AND ec.PPOption=ppo.PPOption AND ec.SupersessionFlag LIKE [SupersessionFlagValue] AND ((ec.ECMCodeBase+'.'+ec.ECMCodeRev) =(ccr.ECMCodeExpr))
 ORDER BY ec.ECMCodeBase, ec.ECMCodeRev;

SELECT DISTINCTROW (ec.ECMCodeBase + '.' + ec.ECMCodeRev) AS ECMCodeExpr, ppo.EngModel AS EngineModel, sco.ApplRating AS Market, ec.CPL AS CPL, (sco.AdvPower + ' hp @ ' + sco.AdvPowerRPM + ' rpm') AS AdvPowerExpr,  (sco.GovernedPower + ' hp @ ' + sco.GovernedPowerRPM + ' rpm') AS AdvPowerExpr, (sco.TorquePeak + ' lb-ft @ ' + sco.TorquePeakRPM + ' rpm') AS TorquePeakExpr, doo.OEMName AS OEMName, doo.OEMModel AS OEMModel, doo.OEMApplication AS ApplicationComments, doo.TransmissionType AS TransmissionType, doo.ThrottleType AS ThrottleType, sco.CalRestr AS PerformanceOptions, sco.CalibrationVoltage AS Voltage, ppo.PPComments AS CPLComments, sco.SCComments AS SCComments, doo.DOComments AS DOComments, ec.FROption AS FROption, ec.SCOption AS SCOption, ec.DOOption AS DOOption,  ccr.result AS CertificationCodes FROM ECMCODE AS ec, DOOPTION AS doo, SCOPTION AS sco, PPOPTION AS ppo, CERTCODE AS cc, ECMAP AS eap, CERTCODERESULT AS ccr
 WHERE c.SCOption=sco.SCOption AND ec.DOOption=doo.DOOption AND ec.PPOption=ppo.PPOption AND ec.ECMPartNumber = [ECMPartNumberValue] AND ((ec.ECMCodeBase)=[eap].[ECMCodeBase]) AND ((ec.ECMCodeRev)=[eap].[ECMCodeRev]) AND ec.SupersessionFlag LIKE [SupersessionFlagValue] AND cc.APOption=eap.APOption AND INSTR([SelectedCertCodesValue], cc.CertCode) > 0 AND ((ec.ECMCodeBase+'.'+ec.ECMCodeRev) =(ccr.ECMCodeExpr))
 ORDER BY ec.ECMCodeBase, ec.ECMCodeRev;

SELECT DISTINCTROW (ec.ECMCodeBase + '.' + ec.ECMCodeRev) AS ECMCodeExpr, ppo.EngModel AS EngineModel, sco.ApplRating AS Market, ec.CPL AS CPL, (sco.AdvPower + ' hp @ ' + sco.AdvPowerRPM + ' rpm') AS AdvPowerExpr,  (sco.GovernedPower + ' hp @ ' + sco.GovernedPowerRPM + ' rpm') AS AdvPowerExpr, (sco.TorquePeak + ' lb-ft @ ' + sco.TorquePeakRPM + ' rpm') AS TorquePeakExpr, doo.OEMName AS OEMName, doo.OEMModel AS OEMModel, doo.OEMApplication AS ApplicationComments, doo.TransmissionType AS TransmissionType, doo.ThrottleType AS ThrottleType, sco.CalRestr AS PerformanceOptions, sco.CalibrationVoltage AS Voltage, ppo.PPComments AS CPLComments, sco.SCComments AS SCComments, doo.DOComments AS DOComments, ec.FROption AS FROption, ec.SCOption AS SCOption, ec.DOOption AS DOOption,  ccr.result AS CertificationCodes FROM ECMCODE AS ec, DOOPTION AS doo, SCOPTION AS sco, PPOPTION AS ppo, CERTCODE AS cc, ECMAP AS eap, CERTCODERESULT AS ccr
 WHERE c.SCOption=sco.SCOption AND ec.DOOption=doo.DOOption AND ec.PPOption=ppo.PPOption AND ec.ECMPartNumber = [ECMPartNumberValue] AND ((ec.ECMCodeBase)=[eap].[ECMCodeBase]) AND ((ec.ECMCodeRev)=[eap].[ECMCodeRev]) AND ec.SupersessionFlag LIKE [SupersessionFlagValue] AND cc.APOption=eap.APOption AND INSTR([SelectedCertCodesValue], cc.CertCode) > 0 AND ((ec.ECMCodeBase+'.'+ec.ECMCodeRev) =(ccr.ECMCodeExpr))
 ORDER BY ec.ECMCodeBase, ec.ECMCodeRev;

SELECT DISTINCTROW (ec.ECMCodeBase + '.' + ec.ECMCodeRev) AS ECMCodeExpr, ppo.EngModel AS EngineModel, sco.ApplRating AS Market, ec.CPL AS CPL, (sco.AdvPower + ' hp @ ' + sco.AdvPowerRPM + ' rpm') AS AdvPowerExpr,  (sco.GovernedPower + ' hp @ ' + sco.GovernedPowerRPM + ' rpm') AS AdvPowerExpr, (sco.TorquePeak + ' lb-ft @ ' + sco.TorquePeakRPM + ' rpm') AS TorquePeakExpr, doo.OEMName AS OEMName, doo.OEMModel AS OEMModel, doo.OEMApplication AS ApplicationComments, doo.TransmissionType AS TransmissionType, doo.ThrottleType AS ThrottleType, sco.CalRestr AS PerformanceOptions, sco.CalibrationVoltage AS Voltage, ppo.PPComments AS CPLComments, sco.SCComments AS SCComments, doo.DOComments AS DOComments, ec.FROption AS FROption, ec.SCOption AS SCOption, ec.DOOption AS DOOption,  ccr.result AS CertificationCodes FROM ECMCODE AS ec, DOOPTION AS doo, SCOPTION AS sco, PPOPTION AS ppo, CERTCODE AS cc, ECMAP AS eap, CERTCODERESULT AS ccr
 WHERE c.SCOption=sco.SCOption AND ec.DOOption=doo.DOOption AND ec.PPOption=ppo.PPOption AND ec.ECMPartNumber = [ECMPartNumberValue] AND ((ec.ECMCodeBase)=[eap].[ECMCodeBase]) AND ((ec.ECMCodeRev)=[eap].[ECMCodeRev]) AND ec.SupersessionFlag LIKE [SupersessionFlagValue] AND cc.APOption=eap.APOption AND INSTR([SelectedCertCodesValue], cc.CertCode) > 0 AND ((ec.ECMCodeBase+'.'+ec.ECMCodeRev) =(ccr.ECMCodeExpr))
 ORDER BY ec.ECMCodeBase, ec.ECMCodeRev;

History.SQL:
SELECT ch.RevisionDate AS RevisionDate,  ( ch.ECMCodeBase + '.' + ch.ECMCodeRev ) AS SoftwareCalibrationPhase, ch.FQOption AS FQHistory,  ch.History AS CalibrationHistory
 FROM CHGHIST AS ch
 WHERE ch.ECMCodeBase = [ECMCodeBaseValue];

SpecialParameters.SQL:
SELECT DISTINCT sf4.IntSpeedSwitch AS IntermediateSpeedSwitch,  sf4.AltTorque AS AlternateTorque,  sf4.AltLowIdle AS AlternateLowIdle,  sf4.OilTempSensor AS OilTemperatureSensor,  sf4.AltDroopPercent AS AlternateDroopPercent,  sf4.AuxShutdownActuator AS AuxiliaryShutdownActuator,  sf4.TorqueBroadcast AS TorqueBroadcast,  sf4.AuxOilPressSensor AS OilPressureSensor,  sf4.CoolantTempSensor AS CoolantTemperatureSensor,  sf4.ElectronicSTC AS ElectronicSTC
 FROM ECMCODE AS ec, SUBFILE4 AS sf4
 WHERE ec.ECMPartNumber = [ECMPartNumberValue] AND ec.ECMCodeBase = [ECMCodeBaseValue] AND ec.ECMCodeRev = [ECMCodeRevValue] AND sf4.Subfile4 = ec.Subfile4;

IsSuperseded.SQL:
SELECT DISTINCTROW (ec.ECMCodeBase + '.' + ec.ECMCodeRev) AS ECMCodeExpr
 FROM ECMCODE AS ec
 WHERE ec.ECMCodeBase = [ECMCodeBaseValue] AND ec.ECMCodeRev = [ECMCodeRevValue] AND ec.ECMPartNumber = [ECMPartNumberValue] AND (ec.SupersessionFlag = 'Y' OR ec.SupersessionFlag = 'E');

SELECT DISTINCTROW (ec.ECMCodeBase + '.' + ec.ECMCodeRev) AS ECMCodeExpr
 FROM ECMCODE AS ec
 WHERE ec.ECMCodeBase = [ECMCodeBaseValue] AND ec.ECMCodeRev = [ECMCodeRevValue] AND ec.ECMPartNumber = [ECMPartNumberValue] AND (ec.SupersessionFlag = 'Y' OR ec.SupersessionFlag = 'E');

SELECT DISTINCTROW (ec.ECMCodeBase + '.' + ec.ECMCodeRev) AS ECMCodeExpr
 FROM ECMCODE AS ec
 WHERE ec.ECMCodeBase = [ECMCodeBaseValue] AND ec.ECMCodeRev = [ECMCodeRevValue] AND ec.ECMPartNumber = [ECMPartNumberValue] AND (ec.SupersessionFlag = 'Y' OR ec.SupersessionFlag = 'E');

CalibrationSelection.SQL:
SELECT DISTINCTROW (ec.ECMCodeBase + '.' + ec.ECMCodeRev) AS ECMCodeExpr, ppo.EngModel AS EngineModel, sco.ApplRating AS Market, ec.CPL AS CPL, (sco.AdvPower + ' hp @ ' + sco.AdvPowerRPM + ' rpm') AS AdvPowerExpr,  (sco.GovernedPower + ' hp @ ' + sco.GovernedPowerRPM + ' rpm') AS GovernedPower, (sco.TorquePeak + ' lb-ft @ ' + sco.TorquePeakRPM + ' rpm') AS TorquePeakExpr, doo.OEMName AS OEMName, doo.OEMModel AS OEMModel, doo.OEMApplication AS ApplicationComments, doo.TransmissionType AS TransmissionType, doo.ThrottleType AS ThrottleType, sco.CalRestr AS PerformanceOptions, sco.CalibrationVoltage AS Voltage, ppo.PPComments AS CPLComments, sco.SCComments AS SCComments, doo.DOComments AS DOComments, ec.FROption AS FROption, ec.SCOption AS SCOption, ec.DOOption AS DOOption,  ccr.result AS CertificationCodes FROM ECMCODE AS ec, DOOPTION AS doo, SCOPTION AS sco, PPOPTION AS ppo, CERTCODE AS cc, ECMAP AS eap, CERTCODERESULT AS ccr
 WHERE ec.SCOption=sco.SCOption AND ec.DOOption=doo.DOOption AND ec.PPOption=ppo.PPOption AND ec.ECMPartNumber = [ECMPartNumberValue] AND ((ec.ECMCodeBase)=[eap].[ECMCodeBase]) AND ((ec.ECMCodeRev)=[eap].[ECMCodeRev]) AND ec.SupersessionFlag LIKE [SupersessionFlagValue] AND cc.APOption=eap.APOption AND INSTR([SelectedCertCodesValue], cc.CertCode) > 0 AND ((ec.ECMCodeBase+'.'+ec.ECMCodeRev) =(ccr.ECMCodeExpr))
 ORDER BY ec.ECMCodeBase, ec.ECMCodeRev;

BaseEngineInformation.SQL:
SELECT ec.EngModel AS EngineModel,  ec.TrainModel AS TrainModel,  ec.CalibrationVoltage AS CalibrationVoltage
 FROM ECMCODE AS ec
 WHERE ec.ECMCodeBase = [ECMCodeBaseValue] AND ec.ECMCodeRev = [ECMCodeRevValue];

History.SQL:
SELECT ch.RevisionDate AS RevisionDate,  ( ch.ECMCodeBase + '.' + ch.ECMCodeRev ) AS ECMCodeExpr,  ch.History AS History
 FROM CHGHIST AS ch
 WHERE ch.ECMCodeBase = [ECMCodeBaseValue];

CalibrationFileName.SQL:
SELECT TOP 1 ec.EngMonSystem+'.PIM' AS FileName
 FROM ECMCODE AS ec
 WHERE ec.ECMCodeBase = [ECMCodeBaseValue] AND ec.ECMCodeRev = [ECMCodeRevValue];
